    private void comboxnumActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_comboxnumActionPerformed
        String opc = (String) comboxnum.getSelectedItem();
        switch (opc) {
            case "0":
                lblTraduccion.setText("Zero");
                break;
            case "1":
                lblTraduccion.setText("One");
                break;
            case "2":
                lblTraduccion.setText("Two");
                break;
            case "3":
                lblTraduccion.setText("Three");
                break;
            case "4":
                lblTraduccion.setText("Four");
                break;
            case "5":
                lblTraduccion.setText("Five");
                break;
            case "6":
                lblTraduccion.setText("Six");
                break;
            case "7":
                lblTraduccion.setText("Seven");
                break;
            case "8":
                lblTraduccion.setText("Eight");
                break;
            case "9":
                lblTraduccion.setText("Nine");
                break;
        }
    }//GEN-LAST:event_comboxnumActionPerformed

    

